var fs = require('fs');
var path = require('path');
var pathConst = require('../../config/path.js');

exports.log = function(msg){
	fs.appendFileSync(path.join(pathConst.APP_PATH,'log/info.log'),msg+'\r\n');
}
exports.error = function(msg){
	fs.appendFileSync(path.join(pathConst.APP_PATH,'log/error.log'),msg+'\r\n');
}