var fs = require('fs');
require('./date');
var _dirLog = __dirname + '/../../log/';
if(!fs.existsSync(_dirLog)){
	fs.mkdirSync(_dirLog,0755);
}
function write(fileName,msg){
	msg = [].slice.call(arguments,1);
	msg.unshift('[ '+(new Date().format('yyyy-MM-dd hh:mm:ss'))+' ]');
	msg.push('\r\n');
	msg.push('\r\n');
	msg = msg.join('\t');
	fs.appendFile(fileName,msg);
}
exports.log = function(msg){
	var args = [].slice.call(arguments);
	args.unshift('./log/log.txt');
	write.apply(this,args);
};
exports.print = function(msg){
	var args = [].slice.call(arguments);
	args.unshift('./log/sys.txt');
	write.apply(this,args);
};